    window.attachEvent('onload',function(){
        var page_tracker =new PageTracker();
        bindDDclick();
        page_tracker.trackPingPHP('http://search.dangdang.com/ping.php?'+'act=view&pos=0_0_0_q&cat=&key=%C1%C4%D5%AB&qinfo=3457_1_60&pinfo=&minfo=&ninfo=&custid=&permid=&ref=&rcount=&type=&t=1506933681000&sell_run=1&searchapi_version=new_search');
    });
}else{
    window.addEventListener('load',function(){
        var page_tracker =new PageTracker();
        bindDDclick();
        page_tracker.trackPingPHP('http://search.dangdang.com/ping.php?'+'act=view&pos=0_0_0_q&cat=&key=%C1%C4%D5%AB&qinfo=3457_1_60&pinfo=&minfo=&ninfo=&custid=&permid=&ref=&rcount=&type=&t=1506933681000&sell_run=1&searchapi_version=new_search');
    },false);
}
function bindDDclick(){
    var all_ele = document.getElementsByTagName('*'),
        page_tracker =new PageTracker(),
        len = 0,
        i = 0,
        all_ele_list,
        j = 0;
    all_ele_list = all_ele;
    for(i = 0,len = all_ele_list.length;i<len;i++){
        if(all_ele_list[i].getAttribute('ddclick') !== null){
            if(document.all){
                all_ele_list[i].attachEvent('onclick',(function(ddclickUrl){
                    return function(){
                        page_tracker.trackPingPHP('http://search.dangdang.com/ping.php?'+ddclickUrl+'&cat=&key=%C1%C4%D5%AB&qinfo=3457_1_60&pinfo=&minfo=&ninfo=&custid=&permid=&ref=&rcount=&type=&t=1506933681000&sell_run=1&searchapi_version=new_search');
                    }
                })(all_ele_list[i].getAttribute('ddclick')));
            }else{
                all_ele_list[i].addEventListener('click',(function(ddclickUrl){
                    return function(){
                        page_tracker.trackPingPHP('http://search.dangdang.com/ping.php?'+ddclickUrl+'&cat=&key=%C1%C4%D5%AB&qinfo=3457_1_60&pinfo=&minfo=&ninfo=&custid=&permid=&ref=&rcount=&type=&t=1506933681000&sell_run=1&searchapi_version=new_search');
                    }
                })(all_ele_list[i].getAttribute('ddclick')),false);
            }
        }
    }
}

