<script type='text/javascript'>
    $(function(){     
        $('#t__cp').keydown(function(e){
            if(e.keyCode==13){
                verifyListPages();
            }
        });
        $('#click_get_page').click(function(){
            verifyListPages();
        });
    });

    function verifyListPages(){
        var page=$.trim($('#t__cp').val());
        var max_page=58;
        re = /^[1-9]*[0-9]*[0-9]$/i; 
        if(!re.test(page)||page==0){                  
            alert('您好，请输入正确页码。');
            return false;                   
        }else{
            if(parseInt(page)>100){
